#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    string s;    cin >> s;    static unordered_map<string, int> m;     if(m.find(s) != m.end()){        int c = m[s];        cout << s << c<< endl;    }    else{        cout << "OK\n";    }    m[s]++; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}