#include <bits/stdc++.h>using namespace std;#define ll long long void solve(){    // LOGIC    ll n;    cin>> n;     vector<ll> a(n), b(n);     for(auto &i:a ) cin >> i;     b = a;     sort(b.begin(), b.end());     vector<ll> ap(n + 1, 0), bp(n + 1, 0);     for(int i = 1; i <= n ; i++){        ap[i] = ap[i - 1] + a[i - 1];        bp[i] = bp[i - 1] + b[i - 1];    }     // for(int i = 0; i <= n ; i++){    //     cout << ap[i] << " ";    // }       ll k;    cin >> k;     while(k--){        ll x,y,z;        cin >> x >> y>> z;        // if(y == 1) y = 2;        if(x & 1){            cout << ap[z] - ap[y - 1] << endl;        }        else{            cout << bp[z] - bp[y - 1] << endl;        }    }    } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from coutsolve();}