#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<int> v(n),c;     for(int i = 0; i < n; i++) cin >> v[i];    c = v;     sort(c.begin(), c.end());    for(int i = 0;i < n; i++){        if(v[i] != c[i]) break;        if(i == n - 1){            cout << "yes\n" << 1 << " " << 1 << endl;            return;        }     }    int i = 0, j = n -1;    while(i < n){        if(c[i] != v[i])break;        i++;    }    while(j >= 0){        if(c[j] != v[j])break;        j--;    }        reverse(v.begin() + i, v.begin() + j + 1);    for(int i = 0;i < n; i++){        if(v[i] != c[i]) {cout << "no\n"; return;}    }    cout << "yes\n" << i + 1 << " " << j + 1 << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}