#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    bool price = false, qual = false;    cin >> n;    for(int i = 0; i< n; i++){        int a, b;        cin >> a >> b;        if(a < b ) qual = true;        if(a > b) price = true;      }     if(price && qual){        cout << "Happy Alex" << endl;    }    else cout << "Poor Alex" << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}