#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);    for(int i = 0; i < n; i++) cin >> v[i];    int k;    cin >> k;    vector<int> v2(k);    for(int i = 0; i < k ; i++) cin >> v2[i];     vector<int> pref(n);    pref[0] = v[0];        for(int i = 1; i < n; i++){        pref[i] = pref[i - 1] + v[i];    }     for(int i = 0; i < k ; i++){        int curr = v2[i];        auto it = lower_bound(pref.begin(), pref.end(), curr);        cout << (it - pref.begin()) + 1<< "\n";            }  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}