#include <bits/stdc++.h>using namespace std; const int MOD = 1e9 + 7; int n, k , val = 1e5;vector<int> v(val + 1), pref(val+ 1);  void solve(){    // LOGIC    int a, b;    cin >> a >> b;    int ans = ( pref[b] - pref[a - 1] ) % MOD;     if(ans < 0) ans += MOD;    cout << ans << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     cin >> n >> k;    for(int i = 1; i < k; i++){        v[i] = 1;    }    v[k] = 2;     for(int i = k + 1; i <= val; i++){        v[i] = (v[i - 1] + v[i - k]) % MOD;    }     for(int i = 1; i<= val; i++){        pref[i] = (pref[i - 1] + v[i]) % MOD;    }     while(n--) solve();}