#include <bits/stdc++.h>using namespace std;#define int long long int tem(int c){    return (c * (c - 1))/2;} void solve(){    // LOGIC    int x, y;    cin >> x >> y;     int sm = 0, bg = 0;     sm += tem(x/ y ) * (y - (x % y));    sm += tem((x /y  + 1)) * (x % y);      bg = tem(x - y + 1);      cout << sm << " " << bg << endl; } signed main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout  solve();}