#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<pair<int, int>> v, ans;        for(int i = 0; i < n; i++ ){        int a , b;        cin >> a >> b;        v.push_back({a, b});    }     sort(v.begin(), v.end());    int small = INT_MIN;    for(auto i: v){        if(i.first > i.second){            if(small <= i.second) small = i.second;            else small = i.first;        }        else{            small = i.first;         }    }    cout << small << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout    solve();}