#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, k;    cin >> n >> k;     vector<int> v(n);     for(auto &i : v) cin >> i;      sort(v.begin(), v.end());     double ans = 0.0;    ans = max(ans, v[0] - 0.0 );    ans = max(ans,  k * 1.0 - v[n - 1] );     for(int i = 1; i < n ; i++){        ans = max(ans, (v[i] - v[i - 1] ) / 2.0);     }      cout << fixed << setprecision(10) << ans << endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}