#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, t;    cin >> n >> t;     vector<int> v(n );     for(int i = 1; i < n ; i++) cin >> v[i];     int i = 1;    while( i < t){        i += v[i];    }    if(i == t) cout << "YES";    else    cout << "NO";} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}