#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int a, b,c,d;    string ans;    cin >> a >> b >> c >> d;     float v = max((b * 3 /10.0), b - (b/250.0) * d);    float m = max((a * 3 /10.0), a - (a/250.0) * c);    if(v == m) ans = "Tie";    else if(v > m) ans = "Vasya";    else ans = "Misha";     cout << ans <<  endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}