#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    string s;    cin >> s;    int u = 0, l = 0;    for(auto i:s){        if(isupper(i)) u++;        else l++;    }    if(u <= l) for(auto &i:s) cout << (char) tolower(i);    else if(u> l) for(auto &i:s) cout << (char) toupper(i);    cout << endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}