#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int a = 0, b = 0, c = 0, n;    cin >> n;    for(int i = 0; i < n; i++){        int x, y, z;        cin >> x >> y >> z;        a += x;        b += y;        c += z;    }    if(a == 0 && b == 0 && c == 0){        cout << "YES\n";    }    else{        cout << "NO\n";    } } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}