#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n * 2);     for(int i = 0; i < (n * 2); i++) cin >> v[i];     sort(v.begin(), v.end());    // for(auto i:v){    //     cout << i << " ";    // }    int ans = INT_MAX;    for(int i = 0; i < (2 * n) ; i++){        for(int j = i + 1; j < 2 * n; j++){            int cur = 0;            vector<int> curr;            for(int k = 0; k < 2 * n; k++){                if(k == i || k == j){                    continue;                }                curr.push_back(v[k]);            }             for(int m = 0; m < curr.size() ;m +=2){                cur += curr[m + 1] - curr[m];             }            ans = min(ans, cur);         }    }    cout << ans << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}