#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, k;    cin >> n >> k;    vector<int> v(n);    // k -= 1;     for(int i = 0; i < n; i++) cin >> v[i];     sort(v.begin(), v.end());     // for(int i = 0; i < k; i++){    //     if(v)    // }     if(k == 0){        if(v[0] > 1) cout << v[0] - 1 << endl;        else cout << "-1\n";    }    else if(k == n){        cout << v[n - 1] << endl;    }    else {        if(v[k- 1] != v[k]) cout << v[k - 1] << endl;        else cout << "-1\n";     } } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}