#include <bits/stdc++.h>using namespace std;  int n, len, start;vector<int> ans;map<int, int> dp;  void solve(){    // LOGIC    int n;    cin >> n;     vector<int> v(n);    for(int i = 0; i < n; i++) cin >> v[i];      for(int i = 0; i < n; i++){        int x = v[i];        dp[x] = max(dp[x], 1);        dp[x] = max(dp[x], dp[x - 1] + 1);         if(len < dp[x]){            len = dp[x];            start = x - len + 1;        }    }     cout << len << endl;    for(int i = 0; i < n; i++){        if(v[i] == start){            ans.push_back(i + 1);            start++;        }    }     for(auto i : ans){        cout << i << " ";    }    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}